          <Route
            path="/login"
            element={user ? <Navigate to="/" /> : <Login1 />}
          />
          <Route
            path="/post/:id"
            element={user ? <Post /> : <Navigate to="/login" />}
          />
        </Routes>
      </div>
    </BrowserRouter>
  );
}

export default App;
